-- SQL statement generated by ChatGPT
SELECT
    a.name,
    avg_salaries.avg_salary
FROM
    departments a
LEFT JOIN (
    SELECT
        b.department_id,
        AVG(b.salary) AS avg_salary
    FROM
        employees b
    GROUP BY
        b.department_id
) avg_salaries ON a.department_id = avg_salaries.department_id
WHERE
    a.location = 'New York'
ORDER BY
    a.name,
    avg_salaries.avg_salary DESC
LIMIT 5 OFFSET 10
